    }

    public function getXCells(){
        return $this->x_cells;
    }
    public function getYCells(){
        return $this->y_cells;
    }
    public function addRover($rover, &$errors = null)
    {
        if ($rover instanceof Rover) {
            //check if position is inside board
            if (($rover->x >= $this->x_cells || $rover->x < 0) || ($rover->y >= $this->y_cells || $rover->y < 0)) {
                $errors[] = 'out';
                return;
            }

            $this->rover = $rover;



            if ($this->board[$rover->y][$rover->x] == 0) {
                $this->board[$rover->y][$rover->x] = $rover;
            }
        } else {
            $errors[] = "Rover expected";
        }
    }
    public function toHtml()
    {   
        // dd($this->board);
        $html = "";
        foreach ($this->board as $row) {
            $html_row = "<div style='display:flex'>";
            foreach ($row as $col) {
                // dd($col === 0, $col);
                // dd($col == "path");
                $html_cell = "<div style='height: " . $this->cell_dim . "px; width:" . $this->cell_dim . "px; text-align: center; border: 0.5px solid #fff'>";
                if ($col instanceof Rover) {
                    // dd(static::$roverColor);
                    $html_cell .= "<div style='background-color: ". static::$roverColor. "; height:100%; width: 100%'></div>";
                } else if ($col === 1) {
                    $html_cell .= "<div style='background-color: ". static::$obstacleColor. "; height:100%; width: 100%'></div>";
                } 
                else if ($col === 0){
                    $html_cell .= "<div style='background-color: #0001; height:100%; width: 100%'></div>";
                }
                else if ($col === "path") {
                    $html_cell .= "<div style='background-color: ". static::$pathColor. "; height:100%; width: 100%'></div>";
                } 
                $html_cell .= "</div>";
                $html_row .= $html_cell;
            }
            $html_row .= "</div>";
            $html .= $html_row;
            // $html .= "<br/>";
        }
        return $html;
    }
